import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '/Users/cadou/Desktop/tabella/eserciziotabella/src/Pages/home.css';
function Home() {
    const [data, setData] = useState([]);
    const [formData, setFormData] = useState({
      name: '',
      region: '',
      country: ''
    });

    useEffect(() => {
      fetchData();
    }, []);

    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:3001/data');
        setData(response.data.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    const handleChange = (e) => {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
      e.preventDefault();
      try {
        const response = await axios.post('http://localhost:3001/data', formData);
        console.log('New data added:', response.data);
        fetchData(); // Refetch data to update the table
        setFormData({ name: '', region: '', country: '' }); // Reset form data
      } catch (error) {
        console.error('Error adding data:', error);
      }
    };

    return (
      <>
        <h2>Data from DB</h2>
        <table className="table-container">
          <thead>
            <tr>
              <th>Name</th>
              <th>Region</th>
              <th>Country</th>
            </tr>
          </thead>
          <tbody>
            {data.length === 0 ? (
              <tr>
                <td colSpan="3">Nessun dato</td>
              </tr>
            ) : (
              data.map(item => (
                <tr key={item.id}>
                  <td>{item.name}</td>
                  <td>{item.region}</td>
                  <td>{item.country}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>

        <h2>Add New Entry</h2>
        <form onSubmit={handleSubmit}>
          <label>
            Name:
            <input type="text" name="name" value={formData.name} onChange={handleChange} />
          </label>
          <label>
            Region:
            <input type="text" name="region" value={formData.region} onChange={handleChange} />
          </label>
          <label>
            Country:
            <input type="text" name="country" value={formData.country} onChange={handleChange} />
          </label>
          <button type="submit">Add</button>
        </form>
      </>
    );
  }
